import React, { useEffect, useState } from 'react'
import './TodoSidebar.css'
import { AiFillPlayCircle } from 'react-icons/ai'
import db from '../firebase.js'

function TodoSidebar({ module_id }) {

    const [todos, setTodos] = useState([]);

    useEffect(() => {
        //console.log(db);
        db.collection('modules').onSnapshot(snapshot => {
            //console.log(snapshot.docs.map(doc=>doc.data()));
            setModules(snapshot.docs.map(doc => ({ id: doc.id, module_data: doc.data()})))
        })
    }, []);

    return (
        <div className="todoSidebar">
            <div className="todos-subheading">
                <p>todos</p>
                <p>+</p>
            </div>
            <div className="searchbar">
                <form>
                    <input placeholder="Search"></input>
                </form>
            </div>
            <div className="todo-card">
                <p>Revise Physics Lesson 1</p>
                <i><AiFillPlayCircle color="#8882F1" size="1.2em" /></i>
            </div>
        </div>
    )
}

export default TodoSidebar
