import React, { useEffect, useState } from 'react'
import './TodoSidebar.css'
import { AiFillPlayCircle } from 'react-icons/ai'
import db from '../firebase.js'

function TodoSidebar({ module_id }) {


    const [todos, setTodos] = useState([]);
    const [searchText, setSearchText] = useState('');
    const [modal, setModal] = useState(false);
    const [modalText, setModalText] = useState('');

    const add_todo = (e) => {

    }

    useEffect(() => {
        //console.log(db);
        db.collection('todos').onSnapshot(snapshot => {
            console.log(snapshot.docs.map(doc=>doc.data()));
            setTodos(snapshot.docs.map(doc => ({ id: doc.id, todo: doc.data()})))
        })
    }, []);

    return (
        <>
        <div className="todoSidebar">
            <div className="todos-subheading">
                <p>todos</p>
                <button onClick={setModal(true)}>ADD</button>
            </div>
            <div className="searchbar">
                <form>
                    <input onChange={(e) => setSearchText(e.target.value)} placeholder="Search"></input>
                </form>
            </div>

            {todos.length == 0 ? "Loading..." : ""}
            {todos.filter((val) => {
                if (searchText == ""){
                    return val
                }
                else if (val.todo.title.toLowerCase().includes(searchText.toLowerCase())){
                    return val
                }
            }).map((todo) => (
                <div key={todo.id} className="todo-card">
                  <p>{todo.todo.title}</p>
                  <i><AiFillPlayCircle color="#8882F1" size="1.2em" /></i>
                </div>
            ))}

            <div className={"module-modal " + (modal ? 'active' : '')}>
                <div className="module-modal-panel">
                    <p>Create a new todo</p>
                    <form>
                        <input type="text" placeholder="chapter name" onChange={(e) => setModalText(e.target.value)} value={modalText} />
                        <div className="actions">
                            <button type="submit" className="btn-secondary" onClick={add_todo}>Add</button>
                            <button className="btn-danger" onClick={(e) => {
                                e.preventDefault()
                                setModalText('')
                                setModal(false)}
                            }>Cancel</button> 
                        </div>
                    </form>
                </div>
            </div>

        </div>

        </>
    )
}

export default TodoSidebar
